import React from 'react';
import "./styles.scss";
import BoardDetails from './boardDetails.js';

class MyBoard extends React.Component {
    constructor(props){
        super(props);
        this.state= {
            title: this.props.title || 'Progress',
            data: this.props.records || []
        }
    }
    static getDerivedStateFromProps(props, state) {
        if (props.records !== state.records) {
          return {
            data: props.records,
          };
        }
        return null;
      }
    render() {
return(
    <div className="boardlayout">
        <div className='boardHeader'>
<span className='expand'>{this.state.title}</span>
       <span className='expand'>...</span>
       </div>
       <div >
       { this.state.data.map((record, i) => {
            return( <BoardDetails key={i} {...record}/>)
        })
    }
       </div>
    </div>
);
    }
}
export default MyBoard;
